'use strict';

var grunt   = require('grunt');
var server  = require('../tasks/lib/server');

exports['livereload'] = {
  'start': function(test) {
    test.expect(2);

    server.start(function(err) {
      test.equal(null, err);

      server.start(function(err) {
        test.equal(true, !!err);
        test.done();
      });
    });
  }
};
